import { Link, NavLink } from 'react-router-dom';
import { useLocation } from "react-router-dom";
import classes from "../../../styles/navbar.module.css";

//import Rect, { NavLink }

function Navbar() {
    const pathname = useLocation().pathname
    return (
        <nav className={classes.navbar_style}>
            {/* <li>
                <Link
                    className={`${pathname === '/' ? classes.active : classes.notActive}`}
                    to="/">Accueil</Link>
            </li> */}
            <NavLink to="/" activeClassName={classes.active}>Accueil</NavLink>
            <NavLink to="/About" activeClassName={classes.active}>À propos</NavLink>
            {/* <li>
                <Link
                    className={`${pathname === '/About' ? classes.active : classes.notActive}`}
                    to="/About" >À propos</Link>
            </li> */}
        </nav>
    );
}

export default Navbar;
