import React, { useState } from "react";
import classes from '../../../styles/Dropdown.module.css'
import arrow from "../../../assets/svg/arrowUp.svg"

const Dropdown = (props) => {

    const dropdown_close = `${classes.dropdown_style} ${classes.close_style}`
    const dropdown_open = `${classes.dropdown_style} ${classes.open_style}`

    const [isOpen, setIsOpen] = useState(false);
    /*function handleClick() {
        setIsOpen(!isOpen);
    }*/

    return (
        <>
            <div className={isOpen ? dropdown_open : dropdown_close}>
                <div onClick={() => setIsOpen(!isOpen)} className={classes.title_style}>
                    {props.title}
                </div>
                <img className={classes.arrow_style} src={arrow} alt="arrow" style={isOpen ? { transform: "rotary(0)" } : { transform: "rotary(180deg)" }} />
            </div>
            {isOpen ? <div className={classes.content_style}>{props.content}</div> : null}
            {console.log(isOpen)}
        </>
    )
};

export default Dropdown;
