import React, { useState } from "react";
import classes from '../../../styles/Dropdown.module.css'
import arrowUp from "../../../assets/svg/arrowUp.svg"

const Dropdown = (props) => {

    const dropdown_style_close = `${classes.dropdown_style} ${classes.close_style}`
    //const dropdown_style_open = `${classes.dropdown_style} ${classes.open_style}`

    const [isOpen, setIsOpen] = useState(false);
    function handleClick() {
        setIsOpen(!isOpen);
    }

    return (
        <div className={dropdown_style_close}>
            <div onClick={handleClick} className={classes.h2_style}>
                {props.title}
            </div>
            <img className={classes.arrow_up_style} src={arrowUp} alt="arrow up" />
            {isOpen ? <p className={classes.p_style}>{props.children}</p> : null}
            {console.log(isOpen)}
        </div>

    )
};

export default Dropdown;
