import React from "react";
import Arrow from "../../../assets/svg/sliderArrow.svg";
import classes from "../../../styles/Carroussel.module.css";

// import Logements from "../../../assets/Json/logements.json"

const Carroussel = (props) => {
    const imgUrlArray = props.pics;
    const maxNbr = props.pics.length - 1;
    // console.log(props.pics);
    // console.log(maxNbr);
    let currImg = 0;
    console.log(`${imgUrlArray[currImg]}`);


    const handleArrows = (direction) => {
        if (direction === 'left') {
            if (currImg === 0) {
                currImg = maxNbr;
                console.log(currImg)
            } else {
                currImg--;
                console.log(currImg)
            }
        } else {
            if (currImg === maxNbr) {
                currImg = 0;
                console.log(currImg)
            } else {
                currImg++;
                console.log(currImg)
            }
        }
        console.log(`${imgUrlArray[currImg]}`);
    }

    return (
        <div className={classes.cntnr_slider_style}>
            <img className={classes.player_style} src={imgUrlArray[currImg]} alt="slider" />
            {maxNbr > 1 ?
                <><div className={classes.leftArrow_style} onClick={() => handleArrows("left")}>
                    <img src={Arrow} alt="left arrow" />
                </div>
                    <div className={classes.rightArrow_style} onClick={() => handleArrows("right")}>
                        <img src={Arrow} alt="right arrow" />
                    </div></>
                : null}
        </div>
    )

};

export default Carroussel;
