import React from "react";
import Arrow from "../../../assets/svg/sliderArrow.svg";
import classes from "../../../styles/Carroussel.module.css";

// import Logements from "../../../assets/Json/logements.json"

const Carroussel = (props) => {
    const imgUrlArray = props.pics;
    const maxNbr = props.pics.length;
    const prevImg = 0;
    const currImg = 0;
    const leftClick = 0;
    const rightClick = 1;

    const handleArrows = () => {
        if (prevImg === 0 && leftClick) {
            currImg = props.pics.length;
        } else if (prevImg != 0 && leftClick) {
            currImg--;
        } else if (prevImg === maxNbr && rightClick) {
            currImg = 0;
        } else if (prevImg != maxNbr && rightClick) {
            currImg++;
        }
        prevImg = currImg;
    }

    return (
        <div className={classes.cntnr_slider_style}>
            <img className={classes.player_style} src={imgUrlArray[currImg]} alt="slider" />
            <img src={Arrow} className={classes.LeftArrow} alt="left arrow" onClick={console.log("LEFT")} />
            <img src={Arrow} className={classes.RighttArrow} alt="right arrow" onClick={console.log("RIGHT")} />
        </div>
    )

};

export default Carroussel;
