import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"
import Thumb from "./Thumb/Thumb"
import classes from "../../../styles/Gallery.module.css"
import logements from "../../../assets/Json/logements.json"
//import fileFetch from "file-fetch"
import axios from "axios"



function Gallery() {
    const [logement, setLogement] = useState([])

    useEffect(() => {
        console.log('loading Json file at: ../../../assets/Json/logements.json')
        axios.get('../../../assets/Json/logements.json')
            .then(response => {
                setLogement(response.logement)
            })
            .catch(error => {
                console.log(error)
            })
    })
    return (
        <section>
            <div className={classes.gallery_style}>
                {logements.map(loc => (
                    <div key={loc.id} className={classes.thumb_style}>
                        <Link to={`../../logements/${loc.id}`}>
                            <Thumb
                                cover={loc.cover}
                                title={loc.title}
                            />
                        </Link>
                    </div>
                ))}
            </div>
        </section>
    )
}

export default Gallery;
