import React, { useState } from "react";
import classes from '../../../styles/Dropdown.module.css'
import arrow from "../../../assets/svg/arrowUp.svg"

const Dropdown = (props) => {

    const dropdown_style_close = `${classes.dropdown_style} ${classes.close_style}`
    //const dropdown_style_open = `${classes.dropdown_style} ${classes.open_style}`

    const [isOpen, setIsOpen] = useState(false);
    function handleClick() {
        setIsOpen(!isOpen);
    }

    return (
        <>
            <div className={dropdown_style_close}>
                <div onClick={handleClick} className={classes.title_style}>
                    {props.title}
                </div>
                <img className={classes.arrow_style} src={arrow} alt="arrow" />
            </div>
            {isOpen ? <span className={classes.content_style}>{props.content}</span> : null}
            {console.log(isOpen)}
        </>
    )
};

export default Dropdown;
