import React from "react";
import classes from "./Thumb.module.css"

const thumb = ({ image, title }) => (
    <div className={classes.thumb_style}>
        <img src={image} alt="logement cover" className={classes.img_style} />
        <div className={classes.overlay_style}></div>
        <h2 className={classes.title_style}>{title}</h2>
    </div>
);

export default thumb;
